  --sidebar-primary-foreground: 0 0% 98%;

  --sidebar-accent: 0 0% 94%;

  --sidebar-accent-foreground: 220 15% 25%;

  --sidebar-ring: 219 85% 35%;

  --popover: 0 0% 100%;

  --popover-foreground: 220 15% 25%;

  --popover-border: 220 15% 85%;

  --primary: 219 85% 35%;

  --primary-foreground: 0 0% 98%;

  --secondary: 0 0% 94%;

  --secondary-foreground: 220 15% 25%;

  --muted: 220 8% 92%;

  --muted-foreground: 220 10% 55%;

  --accent: 220 8% 94%;

  --accent-foreground: 220 15% 25%;

  --destructive: 0 70% 45%;

  --destructive-foreground: 0 0% 98%;

  --input: 220 15% 78%;
  --ring: 219 85% 35%;
  --chart-1: 219 85% 35%;
  --chart-2: 142 70% 45%;
  --chart-3: 38 85% 50%;
  --chart-4: 280 65% 50%;
  --chart-5: 16 75% 50%;

  --font-sans: Open Sans, sans-serif;
  --font-serif: Georgia, serif;
  --font-mono: Menlo, monospace;
  --radius: .5rem; /* 8px */
  --shadow-2xs: 0px 2px 0px 0px hsl(220 15% 15% / 0.02);
  --shadow-xs: 0px 2px 0px 0px hsl(220 15% 15% / 0.03);
  --shadow-sm: 0px 2px 0px 0px hsl(220 15% 15% / 0.03), 0px 1px 2px -1px hsl(220 15% 15% / 0.06);
  --shadow: 0px 2px 0px 0px hsl(220 15% 15% / 0.03), 0px 1px 2px -1px hsl(220 15% 15% / 0.06);
  --shadow-md: 0px 2px 0px 0px hsl(220 15% 15% / 0.04), 0px 2px 4px -1px hsl(220 15% 15% / 0.08);
