import nodemailer from 'nodemailer';

// Create transporter lazily to ensure env vars are loaded
function getTransporter() {
  const gmailUser = process.env.GMAIL_USER;
  const gmailPassword = process.env.GMAIL_APP_PASSWORD;

  // Log which credentials are missing for debugging
  if (!gmailUser || !gmailPassword) {
    const missing = [];
    if (!gmailUser) missing.push('GMAIL_USER');
    if (!gmailPassword) missing.push('GMAIL_APP_PASSWORD');
    throw new Error(`Missing email credentials: ${missing.join(', ')}. Please add them to Replit Secrets and restart the application.`);
  }

  return nodemailer.createTransport({
    host: 'smtp.gmail.com',
    port: 587,
    secure: false,
    auth: {
      user: gmailUser,
      pass: gmailPassword,
    },
    tls: {
      rejectUnauthorized: false
    }
  });
}

// Send OTP email
export async function sendOTPEmail(
  to: string,
  username: string,
  otp: string,
  subject: string = 'Your OTP Code'
): Promise<boolean> {
  try {
    const transporter = getTransporter();
    const mailOptions = {
      from: `"Daily Tracker" <${process.env.GMAIL_USER}>`,
      to,
      subject,
      html: `
        <!DOCTYPE html>
        <html>
        <head>
          <style>
            body { font-family: Arial, sans-serif; line-height: 1.6; color: #333; }
            .container { max-width: 600px; margin: 0 auto; padding: 20px; }
            .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 30px; text-align: center; border-radius: 10px 10px 0 0; }
            .content { background: #f9f9f9; padding: 30px; border-radius: 0 0 10px 10px; }
            .otp-box { background: white; border: 2px solid #667eea; border-radius: 8px; padding: 20px; text-align: center; margin: 20px 0; }
            .otp-code { font-size: 32px; font-weight: bold; color: #667eea; letter-spacing: 5px; }
            .footer { text-align: center; margin-top: 20px; color: #666; font-size: 12px; }
          </style>
        </head>
        <body>
          <div class="container">
            <div class="header">
              <h1>Daily Tracker</h1>
            </div>
            <div class="content">
              <p>Hello <strong>${username}</strong>,</p>
              <p>You requested a One-Time Password (OTP) for your account. Please use the code below to continue:</p>

              <div class="otp-box">
                <div class="otp-code">${otp}</div>
              </div>

              <p><strong>Important:</strong> This code will expire in 5 minutes for security purposes.</p>
              <p>If you didn't request this code, please ignore this email or contact support if you have concerns.</p>

              <p>Best regards,<br><strong>Daily Tracker Team</strong></p>
            </div>
            <div class="footer">
              <p>This is an automated email. Please do not reply to this message.</p>
            </div>
          </div>
        </body>
        </html>
      `,
      text: `Hello ${username},\n\nYour OTP code is: ${otp}\n\nThis code will expire in 5 minutes.\n\nBest regards,\nDaily Tracker Team`,
    };

    const info = await transporter.sendMail(mailOptions);
    console.log(`âœ… Email sent successfully to ${to}:`, info.messageId);
    return true;
  } catch (error: any) {
    console.error(`âŒ Failed to send email to ${to}`);
    console.error(`Error type: ${error.code || error.message || 'Unknown'}`);

    // Provide helpful debugging information based on error type
    if (error.code === 'EAUTH') {
      console.error(`Authentication failed. Please check:`);
      console.error(`  - GMAIL_USER is correct`);
      console.error(`  - GMAIL_APP_PASSWORD is a valid App Password (not regular password)`);
      console.error(`  - 2-Step Verification is enabled on your Google account`);
    } else if (error.code === 'ECONNECTION' || error.code === 'ETIMEDOUT') {
      console.error(`Connection failed. Please check your internet connection.`);
    } else {
      console.error(`Error details:`, error.message);
    }

    return false;
  }
}

// Verify email configuration on startup
export async function verifyEmailConfig(): Promise<boolean> {
  try {
    const transporter = getTransporter();
    await transporter.verify();
    console.log('âœ… Email configuration verified - Ready to send emails');
    return true;
  } catch (error) {
    console.error('âŒ Email configuration error:', error);
    console.warn('\nâš ï¸  To enable email OTP functionality:');
    console.warn('1. Create a Gmail App Password at: https://myaccount.google.com/apppasswords');
    console.warn('2. Add GMAIL_USER and GMAIL_APP_PASSWORD to your Secrets\n');
    return false;
  }
}

