  onDeleteTask: (id: string) => void
}

export function TasksTab({ tasks, onToggleTask, onDeleteTask }: TasksTabProps) {
  const [filter, setFilter] = useState<'all' | 'pending' | 'completed'>('all')
  const [searchQuery, setSearchQuery] = useState('')

  const filteredTasks = tasks.filter(task => {
    const statusMatch = filter === 'all' || 
                       (filter === 'pending' && !task.completed) || 
                       (filter === 'completed' && task.completed)

    if (!statusMatch) return false

    if (!searchQuery.trim()) return true

    const query = searchQuery.toLowerCase()
    const titleMatch = task.title.toLowerCase().includes(query)
    const companyMatch = task.company.toLowerCase().includes(query)

    return titleMatch || companyMatch
  })

  const getTypeColor = (type: Task['type']) => {
    switch (type) {
      case 'job-application': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-200'
      case 'follow-up': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-900 dark:text-yellow-200'
      case 'interview': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-200'
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-200'
    }
  }

  return (
    <div className="p-3 sm:p-6 space-y-4 sm:space-y-8">
      <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-3">
        <h2 className="text-lg sm:text-xl font-semibold" data-testid="text-tasks-title">
          Daily Tasks
        </h2>

        <div className="flex gap-1.5 sm:gap-3 flex-wrap">
          {(['all', 'pending', 'completed'] as const).map((f) => (
            <Button
              key={f}
              variant={filter === f ? 'default' : 'outline'}
              size="sm"
              onClick={() => setFilter(f)}
              data-testid={`button-filter-${f}`}
              className="gap-1 sm:gap-2 text-xs sm:text-sm flex-1 sm:flex-initial"
            >
              {f.charAt(0).toUpperCase() + f.slice(1)}
              <Badge variant={filter === f ? 'secondary' : 'outline'} className="text-xs">
                {f === 'all' ? tasks.length : 
                 f === 'pending' ? tasks.filter(t => !t.completed).length :
                 tasks.filter(t => t.completed).length}
              </Badge>
            </Button>
          ))}
        </div>
      </div>

      {tasks.length > 0 && (
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 w-4 h-4 text-muted-foreground" />
          <Input
            type="text"
            placeholder="Search tasks by title or company..."
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
            className="pl-10"
            data-testid="input-search-tasks"
          />
        </div>
      )}

      {filteredTasks.length === 0 ? (
        <div className="text-center py-8 sm:py-12 text-muted-foreground">
          <Calendar className="mx-auto h-10 w-10 sm:h-12 sm:w-12 mb-3 sm:mb-4 opacity-50" />
          <p className="text-sm sm:text-base px-4" data-testid="text-no-tasks">
            {searchQuery.trim() ? `No tasks found matching "${searchQuery}"` :
             filter === 'all' ? 'No tasks yet. Add jobs from the Jobs tab to get started.' :
             filter === 'pending' ? 'No pending tasks.' :
             'No completed tasks.'}
          </p>
        </div>
      ) : (
        <div className="space-y-4">
          {filteredTasks.map((task) => (
            <Card
              key={task.id}
              className={`hover-elevate transition-opacity ${
                task.completed ? 'opacity-75' : ''
              }`}
              data-testid={`card-task-${task.id}`}
            >
              <CardContent className="p-3 sm:p-4">
                <div className="flex items-start gap-2 sm:gap-3">
                  <Checkbox
                    checked={task.completed}
                    onCheckedChange={() => {
                      onToggleTask(task.id)
                      console.log('Toggled task:', task.title)
                    }}
                    className="mt-0.5 flex-shrink-0"
                    data-testid={`checkbox-task-${task.id}`}
                  />

                  <div className="flex-1 space-y-2 min-w-0">
                    <div className="flex flex-col gap-1">
                      <div className="flex items-start justify-between gap-2">
                        <h3 className={`font-medium text-sm sm:text-base break-words leading-tight ${
                          task.completed ? 'line-through text-muted-foreground' : ''
                        }`}>
                          {task.title}
                        </h3>
                      </div>
                      <p className="text-xs sm:text-sm text-primary break-words">{task.company}</p>
                      <div className="flex flex-wrap items-center gap-1.5 mt-1">
                        <Badge className={`${getTypeColor(task.type)} text-xs`}>
                          {task.type.replace('-', ' ')}
                        </Badge>
                        <span className="text-xs text-muted-foreground">
                          {task.addedDate}
                        </span>
                      </div>
                    </div>

                    <div className="flex gap-2 pt-1">
                      {task.url && (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => window.open(task.url, '_blank')}
                          data-testid={`button-view-task-${task.id}`}
                          className="flex-1 h-8 text-xs"
                        >
                          <ExternalLink className="w-3 h-3 mr-1" />
                          View
                        </Button>
                      )}

                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => {
                          onDeleteTask(task.id)
                          console.log('Deleted task:', task.title)
                        }}
                        className="text-destructive hover:bg-destructive hover:text-destructive-foreground flex-1 h-8 text-xs"
                        data-testid={`button-delete-task-${task.id}`}
                      >
                        <Trash2 className="w-3 h-3 mr-1" />
                        Delete
                      </Button>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          ))}
        </div>
      )}
    </div>
  )
}

client/src/pages/mobile-login.tsx.content:
import { useState } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Phone, ArrowLeft } from "lucide-react";
import { useToast } from "@/hooks/use-toast";
import { useAuth } from "@/lib/auth";
import { Button } from "@/components/ui/button";
import { InputOTP, InputOTPGroup, InputOTPSlot } from "@/components/ui/input-otp";
import "../styles/auth.css";

const phoneSchema = z.object({
  phone: z.string().min(10, "Phone number must be at least 10 digits"),
});

type PhoneForm = z.infer<typeof phoneSchema>;

export default function MobileLoginPage() {
  const [step, setStep] = useState<"phone" | "otp">("phone");
  const [phone, setPhone] = useState("");
  const [otp, setOtp] = useState("");
  const [, setLocation] = useLocation();
  const { toast } = useToast();
  const { refetchAuth } = useAuth();

  const phoneForm = useForm<PhoneForm>({
    resolver: zodResolver(phoneSchema),
  });

  const sendOtpMutation = useMutation({
    mutationFn: async (data: PhoneForm) => {
      const response = await fetch("/api/auth/mobile-login/send-otp", {
        method: "POST",
        body: JSON.stringify(data),
        headers: { "Content-Type": "application/json" },
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error);
      }
      return response.json();
    },
    onSuccess: (_, variables) => {
      setPhone(variables.phone);
      setStep("otp");
      toast({
        title: "OTP Sent to Email",
        description: "Please check your registered email for the verification code",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Failed to send OTP",
        description: error.message,
      });
    },
  });

  const verifyOtpMutation = useMutation({
    mutationFn: async (otpValue: string) => {
      const response = await fetch("/api/auth/mobile-login/verify-otp", {
        method: "POST",
        body: JSON.stringify({ phone, otp: otpValue }),
        headers: { "Content-Type": "application/json" },
        credentials: 'include',
      });
      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error);
      }
      return response.json();
    },
    onSuccess: async () => {
      await refetchAuth();
      setTimeout(() => {
        setLocation("/");
      }, 100);
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Invalid OTP",
        description: error.message,
      });
      setOtp("");
    },
  });

  const handleOtpComplete = (value: string) => {
    setOtp(value);
    if (value.length === 6) {
      verifyOtpMutation.mutate(value);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="curved-shape"></div>
        <div className="curved-shape2"></div>

        <div className="form-box Login" style={{ width: "100%", position: "relative" }}>
          <Button
            variant="ghost"
            onClick={() => setLocation("/auth")}
            className="absolute top-4 left-4 z-10"
            data-testid="button-back-to-login"
          >
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Login
          </Button>

          {step === "phone" && (
            <>
              <h2 className="animation" style={{ "--D": 0, "--S": 21 } as any}>
                Mobile Login
              </h2>
              <p className="text-sm text-muted-foreground mb-4 animation" style={{ "--D": 1, "--S": 22 } as any}>
                OTP will be sent to your registered email
              </p>
              <form onSubmit={phoneForm.handleSubmit((data) => sendOtpMutation.mutate(data))}>
                <div className="input-box animation" style={{ "--D": 2, "--S": 23 } as any}>
                  <input
                    type="text"
                    {...phoneForm.register("phone")}
                    required
                  />
                  <label>Phone Number</label>
                  <Phone className="icon" size={18} />
                </div>

                <div className="input-box animation" style={{ "--D": 3, "--S": 24 } as any}>
                  <button
                    className="btn"
                    type="submit"
                    disabled={sendOtpMutation.isPending}
                  >
                    {sendOtpMutation.isPending ? "Sending OTP..." : "Send OTP"}
                  </button>
                </div>
              </form>
            </>
          )}

          {step === "otp" && (
            <>
              <h2 className="animation" style={{ "--D": 0, "--S": 21 } as any}>
                Enter Verification Code
              </h2>
              <p className="text-sm text-muted-foreground mb-4 animation" style={{ "--D": 1, "--S": 22 } as any}>
                Check your email for the 6-digit code (Phone: {phone})
              </p>
              <div className="flex justify-center mb-6 animation" style={{ "--D": 2, "--S": 23 } as any}>
                <InputOTP maxLength={6} value={otp} onChange={handleOtpComplete}>
                  <InputOTPGroup>
                    <InputOTPSlot index={0} />
                    <InputOTPSlot index={1} />
                    <InputOTPSlot index={2} />
                    <InputOTPSlot index={3} />
                    <InputOTPSlot index={4} />
                    <InputOTPSlot index={5} />
                  </InputOTPGroup>
                </InputOTP>
              </div>
              <div className="input-box animation" style={{ "--D": 3, "--S": 24 } as any}>
                <button
                  className="btn"
                  onClick={() => sendOtpMutation.mutate({ phone })}
                  disabled={sendOtpMutation.isPending}
                >
                  {sendOtpMutation.isPending ? "Resending..." : "Resend OTP"}
                </button>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

